    public static ArrayList<Movie> getListDatamovie() {
        ArrayList<Movie> list = new ArrayList<>();
        for (String[] aData : data) {
            Movie movie = new Movie();
            movie.setName(aData[0]);
            movie.setSinopsis(aData[1]);
            movie.setPhoto(aData[2]);

            list.add(movie);
        }
        return list;
    }
